module.exports = init;

function init(grunt) {
    var config = {
        browserify: {
            dev: {
                src: ['src/angular-bootstrap-modal.module.js'],
                dest: 'dist/angular-bootstrap-modal.js'
            }
        },
        uglify: {
            prod: {
                src: ['dist/angular-bootstrap-modal.js'],
                dest: 'dist/angular-bootstrap-modal.min.js'
            }
        }
    };
    grunt.initConfig(config);

    grunt.loadNpmTasks('grunt-browserify');
    grunt.loadNpmTasks('grunt-contrib-uglify');
    grunt.registerTask('default', ['browserify', 'uglify']);
}
